import React from 'react'

export class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            inputValue: '',
            isEvaluate: false,
            searchBarClass: 'searchbar'
        };
    }

    render() {
        this.getSearchBarClass(this.props.isHomePage);
        return (
                <input 
                    className={this.state.searchBarClass}
                    type="text" 
                    title="Search"
                    placeholder='How can TUO help you...'
                    value={this.state.inputValue}
                    onChange={(evt) => this.handleOnChange(evt)}
                    onKeyDown={(evt) => this.handleOnKeyDown(evt)}
                />
        );
    }

    getSearchBarClass(isHomePage){
        console.log(isHomePage);
        if(isHomePage){
            this.setState({
                searchBarClass: 'searchbar'
            })
        }
    }

    handleOnChange(evt) {
        const value = evt.target.value;

        this.setState({
            inputValue: value
        });
    }

    //Handle Search feature on Enter
    handleOnKeyDown(evt) {
        if(evt.key === 'Enter') {
            console.log(this.state.inputValue);
            if(this.state.value != '') {
                this.handleSearchRequest(this.state.inputValue)
            };
            this.reset();
        };
    }

    //Handle search request
    handleSearchRequest(search) {
        if(search == 'evaluate') {
            console.log('Request Evaluation App');
            this.setState({
                isEvaluate: true
            })
        }else {
            this.setState({
                isEvaluate: false
            })
        }
    }

    reset() {
        this.setState({
            inputValue: ''
        })
    }
}
