import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

import logo from './images/logo.png';
import comingsoon from './images/comingsoon.png';

import Carousel from "nuka-carousel";
import ReactFullpage from '@fullpage/react-fullpage';

const SEL = "custom-section";
const SECTION_SEL = `.${SEL}`;

// NOTE: if using fullpage extensions/plugins put them here and pass it as props.
const pluginWrapper = () => {
  /*
   * require('./fullpage.fadingEffect.min'); // Optional. Required when using the "fadingEffect" extension.
   */
};

//SearBar Component
class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            inputValue: '',
            isEvaluate: false
        };
    }

    render() {
        return (
            <center>
            <div className="bar">
                <input 
                    className="searchbar" 
                    type="text" 
                    title="Search"
                    value={this.state.inputValue}
                    onChange={(evt) => this.handleOnChange(evt)}
                    onKeyDown={(evt) => this.handleOnKeyDown(evt)}
                />
           </div>
           </center>
        );
    }

    handleOnChange(evt) {
        const value = evt.target.value;

        this.setState({
            inputValue: value
        });
    }

    //Handle Search feature on Enter
    handleOnKeyDown(evt) {
        if(evt.key === 'Enter') {
            console.log(this.state.inputValue);
            if(this.state.value != '') {
                this.handleSearchRequest(this.state.inputValue)
            };
            this.reset();
        };
    }

    //Handle search request
    handleSearchRequest(search) {
        if(search == 'evaluate') {
            console.log('Request Evaluation App');
            this.setState({
                isEvaluate: true
            })
        }else {
            this.setState({
                isEvaluate: false
            })
        }
    }

    reset() {
        this.setState({
            inputValue: ''
        })
    }
}

//Logo
function Logo() {
    return <img src={logo} className='logo'/>;
}

//Nuka Carousel
function NukaCarousel() {
    return (
        <Carousel 
        autoplay={true}
        autoplayInterval={5000}
        dragThreshold={.3}
        speed={4000}
        wrapAround={true}
        
        >
          <img className='cimag' src={comingsoon} />
          <img src={comingsoon} />
          <img src={comingsoon} />
          <img src={comingsoon} />
        </Carousel>
      )
}

function EvaluationApp() {
    return(
        <div>
            
        </div>
    )
}

//Home Page
class Home extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            fullpages: [
                {
                    text: 'Section 1'
                },
                {
                    text: 'Section 2'
                }
            ]
        };
    }

    onLeave(origin, destination, direction) {
        console.log("onLeave", { origin, destination, direction });
        // arguments are mapped in order of fullpage.js callback arguments do something
        // with the event
      }

    render() {
        const { fullpages } = this.state;
    
        if (!fullpages.length) {
          return null;
        }
    
        const Menu = () => (
          <div
            className="menu"
            style={{
              position: "fixed",
              top: 0,
              zIndex: 100
            }}
          ></div>
        );
    
        return (
          <div className="App">
            <Menu />
            <ReactFullpage
              debug /* Debug logging */
              // Required when using extensions
              pluginWrapper={pluginWrapper}
              // fullpage options
              licenseKey={"YOUR_KEY_HERE"} // Get one from https://alvarotrigo.com/fullPage/pricing/
              navigation
              anchors={["firstPage", "secondPage", "thirdPage"]}
              sectionSelector={SECTION_SEL}
              onLeave={this.onLeave.bind(this)}
              sectionsColor={this.state.sectionsColor}
              render={(comp) => (
                <ReactFullpage.Wrapper>
                  {fullpages.map(({ text }) => (
                    <div key={text} className={SEL}>
                      <div className="slide">
                        <h3>{text}</h3>
                        <SearchBar />
                      </div>
                    </div>
                  ))}
                </ReactFullpage.Wrapper>
              )}
            />
          </div>
        );
      }




    // render() {
    //     return (
    //         <div >
    //             <div className='toppage'>
    //                 <Logo />
    //                 <div>
    //                     <SearchBar />   
    //                 </div>
    //             </div>
    //             <div className=''>
    //                 <NukaCarousel />
    //             </div>
    //         </div>
    //     );
    // }
    
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Home />);
