import React from 'react'

export class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            inputValue: '',
            isEvaluate: false
        };
    }

    render() {
        return (
            <center>
            {/* <div className="bar">
                <input 
                    className="searchbar" 
                    type="text" 
                    title="Search"
                    value={this.state.inputValue}
                    onChange={(evt) => this.handleOnChange(evt)}
                    onKeyDown={(evt) => this.handleOnKeyDown(evt)}
                />
           </div> */}
              <div>  
            <link href="https://use.fontawesome.com/releases/v5.6.1/css/all.css" rel="stylesheet"/>
            <link href="https://fonts.googleapis.com/css2?family=Lato&display=swap" rel="stylesheet"/>


            <body>
                <div class="search-container">
                    <input 
                        type="text" 
                        name="search" 
                        placeholder="Search..." 
                        class="search-input"
                        value={this.state.inputValue}
                        onChange={(evt) => this.handleOnChange(evt)}
                        onKeyDown={(evt) => this.handleOnKeyDown(evt)}
                        />
                    <a href="#" class="search-btn">
                            <i class="fas fa-search"></i>      
                    </a>
                </div>
            </body>
            </div>
           </center>
        );
    }

    handleOnChange(evt) {
        const value = evt.target.value;

        this.setState({
            inputValue: value
        });
    }

    //Handle Search feature on Enter
    handleOnKeyDown(evt) {
        if(evt.key === 'Enter') {
            console.log(this.state.inputValue);
            if(this.state.value != '') {
                this.handleSearchRequest(this.state.inputValue)
            };
            this.reset();
        };
    }

    //Handle search request
    handleSearchRequest(search) {
        if(search == 'evaluate') {
            console.log('Request Evaluation App');
            this.setState({
                isEvaluate: true
            })
        }else {
            this.setState({
                isEvaluate: false
            })
        }
    }

    reset() {
        this.setState({
            inputValue: ''
        })
    }
}
