import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

import logo from './images/logo.png';

import Carousel from "nuka-carousel";


//SearBar Component
class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            inputValue: ''
        };
    }

    render() {
        return (
            <center>
            <div className="bar">
                <input 
                    className="searchbar" 
                    type="text" 
                    title="Search"
                    value={this.state.inputValue}
                    onChange={(evt) => this.handleOnChange(evt)}
                    onKeyDown={(evt) => this.handleOnKeyDown(evt)}
                />
           </div>
           </center>
        );
    }

    handleOnChange(evt) {
        const value = evt.target.value;

        this.setState({
            inputValue: value
        });
    }

    //Handle Search feature on Enter
    handleOnKeyDown(evt) {
        if(evt.key === 'Enter') {
            console.log(this.state.inputValue);
            this.reset();
        };
    }

    reset() {
        this.setState({
            inputValue: ''
        })
    }
}

//Logo
function Logo() {
    return <img src={logo} className='logo'/>;
}

//Nuka Carousel
function NukaCarousel() {
    return (
        <Carousel>
          <img src="/image1.png" />
          <img src="/image2.png" />
          <img src="/image3.png" />
          <img src="/image4.png" />
          <img src="/image5.png" />
        </Carousel>
      )
}

//Home Page
class Home extends React.Component {
    render() {
        return (
            <div >
                <div className=''>
                    <Logo />
                    <div>
                        <SearchBar />   
                    </div>
                </div>
                <div className=''>
                    <NukaCarousel />
                </div>
            </div>
        );
    }
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Home />);
