import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

import logo from './images/logo.png';
import comingsoon from './images/comingsoon.png';

import Carousel from "nuka-carousel";
import ReactFullpage from '@fullpage/react-fullpage';


//SearBar Component
class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            inputValue: '',
            isEvaluate: false
        };
    }

    render() {
        return (
            <center>
            <div className="bar">
                <input 
                    className="searchbar" 
                    type="text" 
                    title="Search"
                    value={this.state.inputValue}
                    onChange={(evt) => this.handleOnChange(evt)}
                    onKeyDown={(evt) => this.handleOnKeyDown(evt)}
                />
           </div>
           </center>
        );
    }

    handleOnChange(evt) {
        const value = evt.target.value;

        this.setState({
            inputValue: value
        });
    }

    //Handle Search feature on Enter
    handleOnKeyDown(evt) {
        if(evt.key === 'Enter') {
            console.log(this.state.inputValue);
            if(this.state.value != '') {
                this.handleSearchRequest(this.state.inputValue)
            };
            this.reset();
        };
    }

    //Handle search request
    handleSearchRequest(search) {
        if(search == 'evaluate') {
            console.log('Request Evaluation App');
            this.setState({
                isEvaluate: true
            })
        }else {
            this.setState({
                isEvaluate: false
            })
        }
    }

    reset() {
        this.setState({
            inputValue: ''
        })
    }
}

//Logo
function Logo() {
    return <img src={logo} className='logo'/>;
}

//Nuka Carousel
function NukaCarousel() {
    return (
        <Carousel 
        autoplay={true}
        autoplayInterval={5000}
        dragThreshold={.3}
        speed={4000}
        wrapAround={true}
        
        >
          <img className='cimag' src={comingsoon} />
          <img src={comingsoon} />
          <img src={comingsoon} />
          <img src={comingsoon} />
        </Carousel>
      )
}

function EvaluationApp() {
    return(
        <div>
            
        </div>
    )
}

