import React from "react";

export class RegistrationPage extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            inputValue: ''
        };
    }

    render(){
        return(
            <div className="pagestage">
                <input 
                    type="text" 
                    value={this.state.inputValue} 
                    onChange={(evt)=>this.handleOnCHange(evt)}
                />
                <button onClick={(evt)=>this.handlePostPhonenumber()}>Registration</button>
            </div>
        )
    }

    handleOnCHange(evt){
        const value = evt.target.value;
        this.setState({
            inputValue: value,
            isInvitedByPhone: false
        });
    }
    handlePostPhonenumber(){
        const phoneNumber = this.state.inputValue;
        const jsonData = {
            'phonenumber': phoneNumber
        };
        
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(jsonData)
        };

        fetch('http://localhost:3001/phonenumber', requestOptions)
        .then(response => response.json())
        .then(data => this.handleIsInvitedByPhone(data.isInvitedByPhone));
            
            // this.setState({
            //     isInvitedByPhone: data.isInvitedByPhone
            // })
        // ));
        
        console.log('isInvitedByPhone: ' + this.state.isInvitedByPhone);

        this.reset();
    }

    handleIsInvitedByPhone(isInvitedByPhone){
        console.log('data: ' + isInvitedByPhone);
        this.setState({
            isInvitedByPhone: isInvitedByPhone
        })
    }

    reset() {
        this.setState({
            inputValue: ''
        })
    }
}
